import React from 'react';

class Weather extends React.Component{
	render(){

		return(
		 	<div>
		 		{this.props.sohor && this.props.desh && <p>Location: {this.props.sohor}, {this.props.desh}</p>} 
		 		{this.props.tapmatra && <p>Temperature: {this.props.tapmatra}</p>}
		 		{this.props.soitto && <p>Humidity: {this.props.soitto}</p>}
		 		{this.props.bornona && <p>Description: {this.props.bornona}</p>}
		 		{this.props.error && <p>{this.props.error}</p>}		 		
		 	</div>


			);
	}
}

export default Weather;
