import React from 'react';

class Weather extends React.Component{
	render(){

		return(
		 	<div>
		 		{this.props.city && this.props.country && <p>Location: {this.props.sam}, {this.props.jodu}</p>} 
		 		{this.props.ram && <p>Temperature: {this.props.ram}</p>}
		 		{this.props.modu && <p>Humidity: {this.props.modu}</p>}
		 		{this.props.kodu && <p>Description: {this.props.kodu}</p>}		 		
		 	</div>


			);
	}
}

export default Weather;
